var suggestions=document.getElementById('suggestions');var userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(e){if(e.keyCode===191){e.preventDefault();userinput.focus();}
if(e.keyCode===27){userinput.blur();suggestions.classList.add('d-none');}}
document.addEventListener('click',function(event){var isClickInsideElement=suggestions.contains(event.target);if(!isClickInsideElement){suggestions.classList.add('d-none');}});document.addEventListener('keydown',suggestionFocus);function suggestionFocus(e){const focusableSuggestions=suggestions.querySelectorAll('a');const focusable=[...focusableSuggestions];const index=focusable.indexOf(document.activeElement);let nextIndex=0;if(e.keyCode===38){e.preventDefault();nextIndex=index>0?index-1:0;focusableSuggestions[nextIndex].focus();}
else if(e.keyCode===40){e.preventDefault();nextIndex=index+1<focusable.length?index+1:index;focusableSuggestions[nextIndex].focus();}}
(function(){var index=new FlexSearch({preset:'score',cache:true,doc:{id:'id',field:['title','description','content',],store:['href','title','description',],},});var docs=[{id:0,href:"/docs/components/tiles/",title:"Tiles",description:"",content:"\u003ch2 id=\"page-status\"\u003ePage Status\u003c/h2\u003e\n\u003cp\u003eThis page is currently under construction. Use our \u003ca href=\"https://promega.formstack.com/forms/nebula_contact\"\u003eContact Form\u003c/a\u003e if you have questions or feedback.\u003c/p\u003e\n\u003ch2 id=\"overview\"\u003eOverview\u003c/h2\u003e\n\u003cp\u003eTiles are large, clickable elements. They are usually used in a group of related items, such as child items under a parent item or resources for the same product/topic. They are distinct from \u003ca href=\"#\" class=\"red-link\"\u003eCards\u003c/a\u003e\n in that they only link to one target instead of multiple targets.\u003c/p\u003e\n\u003ch3 id=\"tile-types\"\u003eTile Types\u003c/h3\u003e\n\u003ch4 id=\"resource-tiles\"\u003eResource Tiles\u003c/h4\u003e\n\u003cul\u003e\n\u003cli\u003eTiles are built automatically using content stored on the linked item\n\u003cul\u003e\n\u003cli\u003eIcon, label, heading, and background image can be changed by editing the linked item, but edits will be displayed everywhere the linked item is shown in a tile\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003cli\u003eNo control over styling (CSS, colors, etc.)\u003c/li\u003e\n\u003cli\u003ePrimarily used for sharing related resources to a central topic or theme\u003c/li\u003e\n\u003cli\u003eExample: \u003ca href=\"https://www.promega.com/cell-biology/#related-resources\"\u003eCell Biology Resources\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch4 id=\"descendants-grid-tiles-v1\"\u003eDescendants Grid Tiles (v1)\u003c/h4\u003e\n\u003cul\u003e\n\u003cli\u003eBuilt automatically based on children of parent item\u003c/li\u003e\n\u003cli\u003eContent shown on tile is pulled from child item (heading and description)\u003c/li\u003e\n\u003cli\u003eOne color for background\u003c/li\u003e\n\u003cli\u003eNo control over styling\u003c/li\u003e\n\u003cli\u003eExample: \u003ca href=\"https://www.promega.com/cell-biology/#descendants-grid\"\u003eCell Biology Product Categories\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch4 id=\"descendants-grid-tiles-v2\"\u003eDescendants Grid Tiles (v2)\u003c/h4\u003e\n\u003cul\u003e\n\u003cli\u003eUse on the home page and promotions landing page\u003c/li\u003e\n\u003cli\u003e\u003c/li\u003e\n\u003cli\u003eExample: \u003ca href=\"https://www.promega.com/products/promotional-offers/\"\u003ePromotional Offers\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch4 id=\"custom-built-tiles\"\u003eCustom-Built Tiles\u003c/h4\u003e\n\u003cul\u003e\n\u003cli\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch4 id=\"product-tiles\"\u003eProduct Tiles\u003c/h4\u003e\n\u003cul\u003e\n\u003cli\u003eOften referred to as \u0026ldquo;Product Cards\u0026rdquo; as the design is distinct from other tiles on the site\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"guidelines\"\u003eGuidelines\u003c/h2\u003e\n\u003ch3 id=\"use-tiles-when\"\u003eUse Tiles When\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eDisplaying child items (e.g., sub-pages or sub-categories under a parent category).\u003c/li\u003e\n\u003cli\u003eDisplaying related items/content under a specific topic or theme.\u003c/li\u003e\n\u003cli\u003eDrilling down the path to product.\u003c/li\u003e\n\u003cli\u003eLinking to a single destination.\u003c/li\u003e\n\u003cli\u003eDisplaying less content, including one of each:\n\u003cul\u003e\n\u003cli\u003eHeading\u003c/li\u003e\n\u003cli\u003eImage\u003c/li\u003e\n\u003cli\u003eIcon\u003c/li\u003e\n\u003cli\u003eParagraph text\u003c/li\u003e\n\u003cli\u003eCTA\u003c/li\u003e\n\u003c/ul\u003e\n\u003c/li\u003e\n\u003cli\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id=\"dont-use-tiles-when\"\u003eDon\u0026rsquo;t Use Tiles When\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eLinking to multiple destinations. Instead, use a \u003ca href=\"#\" class=\"red-link\"\u003elink\u003c/a\u003e\n or \u003ca href=\"/docs/components/buttons\"\u003ebutton\u003c/a\u003e.\u003c/li\u003e\n\u003cli\u003eLinking to a file download. Instead, use a \u003ca href=\"#\" class=\"red-link\"\u003elink\u003c/a\u003e\n, \u003ca href=\"/docs/components/buttons\"\u003ebutton\u003c/a\u003e, or the \u003ca href=\"#\" class=\"red-link\"\u003eFile Download\u003c/a\u003e\n component.\u003c/li\u003e\n\u003cli\u003eDisplaying complex content, e.g., multiple paragraphs, images, headings and subheadings.\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id=\"do--dont\"\u003eDo / Don\u0026rsquo;t\u003c/h3\u003e\n\u003c!-- \u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/do-bg-gradient.png\" class=\"guideline-img\" /\u003e\n\nDo make sure buttons are visible against backgrounds.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/dont-busy-bg.png\" class=\"guideline-img\" /\u003e\n\nDon't use an outline button on a busy background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\n\n\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/do-contrast.png\" class=\"guideline-img\" /\u003e\n\n\nDo check contrast between button and background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/dont-contrast.png\" class=\"guideline-img\" /\u003e\n\n\nDon't use a button against a sol background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\n\n\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/do-align-1.png\" class=\"guideline-img\" /\u003e\n\n\u003cimg src=\"/images/components/buttons/do-align-2.png\" class=\"guideline-img\" /\u003e\n\n\nMatch button and text alignment.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/dont-align-1.png\" class=\"guideline-img\" /\u003e\n\n\u003cimg src=\"/images/components/buttons/dont-align-2.png\" class=\"guideline-img\" /\u003e\n\n\nDon't vary alignment of content within a container.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n --\u003e\n"},{id:1,href:"/docs/email-style-guide/best-practices/",title:"Best Practices",description:"",content:"\u003ch2 id=\"can-spam-act\"\u003eCAN-SPAM Act\u003c/h2\u003e\n\u003cp\u003eEach country has its own set of rules and regulations for sending marketing emails. To maintain a good email sending reputations and avoid being blacklisted, follow these regulations.\u003c/p\u003e\n\u003ch3 id=\"us-requirements\"\u003eUS Requirements\u003c/h3\u003e\n\u003col\u003e\n\u003cli\u003eDon’t lie in the subject line. The subject should reflect the content of the email.\u003c/li\u003e\n\u003cli\u003eAlways include company name and physical address.\u003c/li\u003e\n\u003cli\u003eProvide a clear way for the recipient to unsubscribe.\u003c/li\u003e\n\u003cli\u003eBe honest and don’t send content to individuals that did not ask to receive the content – \u003cstrong\u003eGDPR!\u003c/strong\u003e\u003c/li\u003e\n\u003c/ol\u003e\n\u003cp\u003eTo learn more about email spam laws, visit \u003ca href=\"https://www.litmus.com/blog/the-ultimate-guide-to-international-email-law-infographic/\"\u003eThe Ultimate Guide to International Email Law\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id=\"content-and-creation\"\u003eContent and Creation\u003c/h2\u003e\n\u003cp\u003eGetting a recipient to open an email is hard enough, but sending an email that doesn’t display properly or contain relevant information, makes it even harder.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eAll Email Clients Are Not Made Equal, Nor Are Recipients\u003c/strong\u003e\u003c/p\u003e\n\u003cp\u003eEach email client can display elements of an email differently, or not at all.\u003c/p\u003e\n\u003cp\u003eHere are a few tips:\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eDon\u0026rsquo;t use background images or videos\u003c/strong\u003e -\nBackground images and videos do not render in most email clients.\nInstead, save the background image with the text and other elements embedded, and then place the image in the email. For videos, save an image with a play icon on it and link the image to where the video is hosted.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eKeep it simple stupid (KISS)\u003c/strong\u003e -\nEmails should contain a clear and concise message that leads the recipient to a call-to-action. This ensures the highest engagement rate.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eTarget your sends\u003c/strong\u003e -\nIt’s tempting to send an email to all of your contacts, but segmenting them by interest and previous engagement will ensure the best results.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eTest and test again\u003c/strong\u003e -\nBe sure your email is rendering properly in the most\npopular email clients.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eA/B Testing\u003c/strong\u003e -\nThis is the process of sending two or more emails to a small percentage of your contacts. A winner is determined and the remaining percentage of contacts will receiving the most engaging email. This is a great way to understand the type of design or messaging that is most relevant to your contacts.\u003c/p\u003e\n\u003ch2 id=\"naming-conventions\"\u003eNaming Conventions\u003c/h2\u003e\n\u003cp\u003eHow to label your emails and other assets in Dynamics for Marketing.\u003c/p\u003e\n\u003cp\u003eFor organization purposes and easy searching, use the following name convention:\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003e[Branch or Department Code]-[Year]-[Main Descriptor]-[Secondary Descriptor]-[Project Number (if applicable)]\u003c/strong\u003e\u003c/p\u003e\n\u003cimg src=\"/images/email-style-guide/best-practices/naming-convention-example.png\" class=\"guideline-img\" /\u003e\n\n"},{id:2,href:"/docs/components/buttons/",title:"Buttons",description:"",content:"\u003ch2 id=\"overview\"\u003eOverview\u003c/h2\u003e\n\u003cp\u003eWe use our primary brand color Sol site-wide to help users quickly identify available actions.\u003c/p\u003e\n\u003ch3 id=\"solid--primary\"\u003eSolid / Primary\u003c/h3\u003e\n\u003cp\u003eUsed for primary actions on the page. Uses solid sol background to draw user\u0026rsquo;s eye.\u003c/p\u003e\n\u003cp\u003eWe recommend only using one solid/primary action per major page section.\u003c/p\u003e\n\u003ch4 id=\"live-example\"\u003eLive Example\u003c/h4\u003e\n\u003cp\u003e\u003cem\u003eNote: This represents a future design that we are working to implement.\u003c/em\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"wvoXQEq\"\n    data-user=\"catertot\"\n    data-height=\"130px\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\u003c/p\u003e\n\u003ch3 id=\"outline--secondary\"\u003eOutline / Secondary\u003c/h3\u003e\n\u003cp\u003eUsed for supporting or non-primary page actions (e.g., additional resources, related products)\u003c/p\u003e\n\u003ch4 id=\"live-example-1\"\u003eLive example\u003c/h4\u003e\n\u003cp\u003e\u003cem\u003eNote: This represents a future design that we are working to implement.\u003c/em\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"NWbmVPW\"\n    data-user=\"catertot\"\n    data-height=\"130px\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\u003c/p\u003e\n\u003c!-- ### Link Style --\u003e\n\u003c!-- Do we want this? Idk --\u003e\n\u003c!-- ### Full Width\n\nDisplays at full width of its container.\n\n[example here] --\u003e\n\u003c!-- ### Accessibility\n[under construction] --\u003e\n\u003ch2 id=\"guidelines\"\u003eGuidelines\u003c/h2\u003e\n\u003ch3 id=\"use-buttons-when\"\u003eUse Buttons When\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eTriggering an action\u003c/li\u003e\n\u003cli\u003eEnabling a “final” action (e.g., Check Out, Add to Cart)\u003c/li\u003e\n\u003cli\u003eMoving forward or backward through a flow\u003c/li\u003e\n\u003cli\u003eSubmitting a form or requested information\u003c/li\u003e\n\u003cli\u003eConfirming completion of a flow or cancelling out of it\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id=\"dont-use-buttons-when\"\u003eDon\u0026rsquo;t Use Buttons When\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eTaking users to a different section of the same page. Instead, use \u003ca href=\"#\" class=\"red-link\"\u003eLinks\u003c/a\u003e\n.\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id=\"do--dont\"\u003eDo / Don\u0026rsquo;t\u003c/h3\u003e\n\u003cp\u003e\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/do-bg-gradient.png\" class=\"guideline-img\" /\u003e\n\nDo make sure buttons are visible against backgrounds.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/dont-busy-bg.png\" class=\"guideline-img\" /\u003e\n\nDon\u0026rsquo;t use an outline button on a busy background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\u003c/p\u003e\n\u003cp\u003e\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/do-contrast.png\" class=\"guideline-img\" /\u003e\n\u003c/p\u003e\n\u003cp\u003eDo check contrast between button and background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/dont-contrast.png\" class=\"guideline-img\" /\u003e\n\u003c/p\u003e\n\u003cp\u003eDon\u0026rsquo;t use a button against a sol background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\u003c/p\u003e\n\u003cp\u003e\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/do-align-1.png\" class=\"guideline-img\" /\u003e\n\n\u003cimg src=\"/images/components/buttons/do-align-2.png\" class=\"guideline-img\" /\u003e\n\u003c/p\u003e\n\u003cp\u003eMatch button and text alignment.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/dont-align-1.png\" class=\"guideline-img\" /\u003e\n\n\u003cimg src=\"/images/components/buttons/dont-align-2.png\" class=\"guideline-img\" /\u003e\n\u003c/p\u003e\n\u003cp\u003eDon\u0026rsquo;t vary alignment of content within a container.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\u003c/p\u003e\n\u003c!-- ## Modifiers --\u003e\n\u003c!-- Here's info on the Sitecore modifiers used to create buttons. --\u003e\n"},{id:3,href:"/docs/prologue/introduction/",title:"Introduction",description:"An introduction to the Nebula design system",content:"\u003cp\u003eNebula is the design system for Promega, a biotech company based in Madison, WI, USA. It is a central resource for component information, design rationale, and best practices.\u003c/p\u003e\n\u003cp\u003ePromega.com uses Sitecore as our content management system. Sitecore is managed by the WUX team in Marketing Services.\u003c/p\u003e\n\u003ch2 id=\"overview-of-site-sections\"\u003eOverview of Site Sections\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003e\u003cstrong\u003eGetting Started\u003c/strong\u003e: This section includes information about Nebula itself, as well as basic information about our site. Check here for browser support, breakpoints, and additional resources.\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eFoundations\u003c/strong\u003e: Here\u0026rsquo;s where we share our design rationale for foundational elements. Some information can be found on our \u003ca href=\"https://promega.widencollective.com/portals/rrxinhb5/PromegaBrand\"\u003eBrand Portal\u003c/a\u003e, but we use Nebula to further define web-specific styles and patterns.\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eComponents\u003c/strong\u003e: Detailed information on our Sitecore components. This includes styles and variants, modifier usage, and best practices.\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"design-system-patterns\"\u003eDesign System Patterns\u003c/h2\u003e\n\u003cp\u003eWe use \u003ca href=\"#\" class=\"red-link\"\u003ered links\u003c/a\u003e\n to indicate pages that are planned but have not been fully completed.\u003c/p\u003e\n"},{id:4,href:"/docs/getting-started/browser-info/",title:"Browser Info",description:"",content:"\u003ch2 id=\"supported-browsers\"\u003eSupported Browsers\u003c/h2\u003e\n\u003cp\u003eThe promega.com website supports the following browsers:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eChrome\u003c/li\u003e\n\u003cli\u003eFirefox\u003c/li\u003e\n\u003cli\u003eSafari\u003c/li\u003e\n\u003cli\u003eEdge\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"display-breakpoints\"\u003eDisplay Breakpoints\u003c/h2\u003e\n\u003cp\u003ePromega.com has six breakpoints, named for science stuff.\u003c/p\u003e\n\u003ctable\u003e\n\u003cthead\u003e\n\u003ctr\u003e\n\u003cth style=\"text-align:left\"\u003eBreakpoint Name\u003c/th\u003e\n\u003cth style=\"text-align:center\"\u003eRange\u003c/th\u003e\n\u003c/tr\u003e\n\u003c/thead\u003e\n\u003ctbody\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eGene\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e0-400px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eChromosome\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e401-480px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eNucleus\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e481-600px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eCell\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e601-768px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eColony\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e769-1024px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eOrganism\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e1025-1280px\u003c/td\u003e\n\u003c/tr\u003e\n\u003c/tbody\u003e\n\u003c/table\u003e\n"},{id:5,href:"/docs/email-style-guide/anatomy-of-an-email/",title:"Anatomy of an Email",description:"",content:"\u003ch2 id=\"parts-of-an-email\"\u003eParts of an Email\u003c/h2\u003e\n\u003cimg src=\"/images/email-style-guide/anatomy-of-an-email/anatomy-of-an-email.png\" class=\"guideline-img\" /\u003e\n\n"},{id:6,href:"/docs/foundations/color/",title:"Color",description:"",content:"\u003ch2 id=\"promega-brand\"\u003ePromega Brand\u003c/h2\u003e\n\u003cp\u003eInformation on Promega\u0026rsquo;s brand colors can be found on the \u003ca href=\"https://promega.widencollective.com/portals/ipuxspqm/CopyofColorPromegaBrand\"\u003eBrand Portal\u003c/a\u003e.\u003c/p\u003e\n"},{id:7,href:"/docs/prologue/release-notes/",title:"Release Notes",description:"",content:"\u003cp\u003eThis page will list details about our updates and releases to the system.\u003c/p\u003e\n\u003ch2 id=\"latest-release\"\u003eLatest Release\u003c/h2\u003e\n\u003ch3 id=\"v10---april-2021\"\u003ev1.0 - April 2021\u003c/h3\u003e\n\u003cp\u003eOur initial release! After many months of work, we\u0026rsquo;re excited to unveil the Nebula design system.\u003c/p\u003e\n\u003cp\u003eOur first release consists of:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eSetting up the site (introductory content, contact forms, overall purpose)\u003c/li\u003e\n\u003cli\u003eDefining button components\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"future-releases\"\u003eFuture Releases\u003c/h2\u003e\n\u003cp\u003eFor our next release, we\u0026rsquo;re working on:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eAccessibility guidelines and best practices\u003c/li\u003e\n\u003cli\u003eText link styles\u003c/li\u003e\n\u003cli\u003eTile components\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eWant to request a specific component or feature? Use our \u003ca href=\"https://promega.formstack.com/forms/nebula_contact\"\u003eContact Form\u003c/a\u003e.\u003c/p\u003e\n"},{id:8,href:"/docs/foundations/typography/",title:"Typography",description:"",content:"\u003ch2 id=\"promega-brand\"\u003ePromega Brand\u003c/h2\u003e\n\u003cp\u003eInformation on Promega\u0026rsquo;s brand typography can be found on the \u003ca href=\"https://promega.widencollective.com/portals/op7opfpe/TypographyPromegaBrand\"\u003eBrand Portal\u003c/a\u003e.\u003c/p\u003e\n"},{id:9,href:"/docs/email-style-guide/email-styles/",title:"Email Styles",description:"",content:"\u003ch2 id=\"font-family\"\u003eFont Family\u003c/h2\u003e\n\u003cp\u003eFor all email messages, \u003cstrong\u003euse Arial as the font family\u003c/strong\u003e.\u003c/p\u003e\n\u003cp\u003eArial is a default web \u0026amp; system font. By using Arial, it assures that our emails are consistent and display as expected on multiple platforms.\u003c/p\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"abfa2d68e52265f397dd0bc3511e8a54\"\n    data-user=\"catertot\"\n    data-height=\"450px\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\n\u003ch2 id=\"type-styles\"\u003eType Styles\u003c/h2\u003e\n\u003cp\u003ese these default type styles to assures that our emails are consistent and display as expected on multiple platforms.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eTo view the latest email type styles, refer to the “Promega Main Template” in Dynamics for Marketing (D4M).\u003c/strong\u003e\u003c/p\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"bf473cbb541986a96c13a1ce6b86767c\"\n    data-user=\"catertot\"\n    data-height=\"880px\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\n\u003ch2 id=\"colors\"\u003eColors\u003c/h2\u003e\n\u003cp\u003e\u003cstrong\u003ePromega Sol\u003c/strong\u003e is our brand color and should be as the focal color and primary call to action button color.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eSecondary colors\u003c/strong\u003e are meant to be used as accent colors to complement Promega Sol.\u003c/p\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"243bf602b3c351c23933b6162fe213ea\"\n    data-user=\"catertot\"\n    data-height=\"640px\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\n\u003ch2 id=\"button-styles\"\u003eButton Styles\u003c/h2\u003e\n\u003cp\u003eThe following button styles are recommended for any call-to-action.\u003c/p\u003e\n\u003cp\u003eFor all primary call-to-action buttons, use the \u003cstrong\u003ePrimary Button\u003c/strong\u003e button style.\u003c/p\u003e\n\u003cp\u003eIf a secondary action is required, choose from one of the \u003cstrong\u003eSecondary Button\u003c/strong\u003e styles, or use a text link.\u003c/p\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"196a1ca60a76358d88e725e52ad82452\"\n    data-user=\"catertot\"\n    data-height=\"520px\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\n\u003ch2 id=\"header-styles\"\u003eHeader Styles\u003c/h2\u003e\n\u003cp\u003eThe following style is suggested for use on all Promega email sends.\u003c/p\u003e\n\u003cp\u003eUsing this header will create consistency among all global email communication regardless of content.\u003c/p\u003e\n\u003cimg src=\"/images/email-style-guide/email-styles/header-style.png\" class=\"guideline-img\" /\u003e\n\n\u003ch2 id=\"footer-style\"\u003eFooter Style\u003c/h2\u003e\n\u003cp\u003eThe following footer style is recommended to create consistency and a responsiveness.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eDo not forget\u003c/strong\u003e to include the company address and an unsubscribe link.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eFont size\u003c/strong\u003e: 10px\u003c/p\u003e\n\u003cimg src=\"/images/email-style-guide/email-styles/footer-style.png\" class=\"guideline-img\" /\u003e\n\n"},{id:10,href:"/docs/prologue/contact-us/",title:"Contact Us",description:"",content:"\u003ch2 id=\"design-advisory-guild\"\u003eDesign Advisory Guild\u003c/h2\u003e\n\u003cp\u003eThe Design Advisory Guild (DAG) is a cross-functional team in Marketing Services. Blending our expertise in design, front-end development, and UX, our goal is to create a living resource for designing for Promega on the web.\u003c/p\u003e\n\u003ch3 id=\"current-guild-members\"\u003eCurrent Guild Members\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eCate Amery\u003c/li\u003e\n\u003cli\u003eSean Rudi\u003c/li\u003e\n\u003cli\u003eJamie Xiong\u003c/li\u003e\n\u003cli\u003eJenna Stormberg\u003c/li\u003e\n\u003cli\u003eJason Ober\u003c/li\u003e\n\u003cli\u003eMax Stevenson\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"contact-us\"\u003eContact Us\u003c/h2\u003e\n\u003cp\u003e\u003ca href=\"https://promega.formstack.com/forms/nebula_contact\"\u003eContact Form\u003c/a\u003e\u003c/p\u003e\n\u003cp\u003eUse this form to:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eShare feedback or ask questions\u003c/li\u003e\n\u003cli\u003eRequest a feature or content\u003c/li\u003e\n\u003cli\u003eReport an issue with Nebula\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:11,href:"/docs/getting-started/resources/",title:"Resources",description:"",content:"\u003ch2 id=\"promega-brand-portal\"\u003ePromega Brand Portal\u003c/h2\u003e\n\u003cp\u003eCheck out Promega\u0026rsquo;s \u003ca href=\"https://promega.widencollective.com/portals/rrxinhb5/PromegaBrand\" target=\"_blank\"\u003eBrand Portal\u003c/a\u003e\u003c/p\u003e\n"},{id:12,href:"/docs/email-style-guide/email-image-guidelines/",title:"Image Guidelines",description:"",content:"\u003ch2 id=\"image-optimization\"\u003eImage Optimization\u003c/h2\u003e\n\u003ch3 id=\"sizing-images-for-use\"\u003eSizing Images for Use\u003c/h3\u003e\n\u003cp\u003eAll email images should be created at 72ppi and sized to fit the content area.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eExample\u003c/strong\u003e - An email is 640px wide. If you are creating hero image to span the width of the email, the image you are creating should be 640px wide and whatever height you have determined. An image that is placed in a two column row should be no wider than 400px. 400px ensures that the image will still look good on a mobile device when it increases in size.\nImages should be created at the appropriate file size without losing clarity. This can be achieved through the image export settings in Photoshop.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eAcceptable image formats\u003c/strong\u003e:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003e.png – Used for transparent background images. Export as an 8bit image to reduce file size.\u003c/li\u003e\n\u003cli\u003e.gif – Used for limited color images and animations. File sizes should be kept under 75kb.\u003c/li\u003e\n\u003cli\u003e.jpg – Used for all full color images and has the best file size control options. Keep files sizes under 150kb for header images and 75kb for body images.\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eAfter exporting your image for the web, run your image files through an optimization tool.\u003c/p\u003e\n\u003ch3 id=\"image-optimization-tools\"\u003eImage optimization tools\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eFor PC or Mac: \u003ca href=\"https://tinypng.com/\"\u003ehttps://tinypng.com/\u003c/a\u003e\u003c/li\u003e\n\u003cli\u003eFor Mac only: \u003ca href=\"https://imageoptim.com/mac\"\u003ehttps://imageoptim.com/mac\u003c/a\u003e\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003e\u003cstrong\u003eRemember: The larger the image file, the longer it the email takes to load and the more mobile data your user will have to use.\u003c/strong\u003e\u003c/p\u003e\n\u003ch2 id=\"optimization-demo-video\"\u003eOptimization Demo Video\u003c/h2\u003e\n\u003cp\u003eLearn more about image optimization by watching \u003ca href=\"https://promega-my.sharepoint.com/personal/sean_rudi_promega_com/_layouts/15/onedrive.aspx?id=%2Fpersonal%2Fsean%5Frudi%5Fpromega%5Fcom%2FDocuments%2FDemo%20Videos%2FEmail%20Image%20Optimization%2Emp4\u0026amp;parent=%2Fpersonal%2Fsean%5Frudi%5Fpromega%5Fcom%2FDocuments%2FDemo%20Videos\u0026amp;originalPath=aHR0cHM6Ly9wcm9tZWdhLW15LnNoYXJlcG9pbnQuY29tLzp2Oi9wL3NlYW5fcnVkaS9FYzFEX1h3SzQ4dEt1dFh6Ul96SjRvZ0JMZjNuS2YwOE5aWHlGamJRcW5SZnBBP3J0aW1lPWU4MXdGcll4MlVn\"\u003ethis video\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id=\"image-upload-keywords-and-alt-tags\"\u003eImage Upload, Keywords, and Alt Tags\u003c/h2\u003e\n\u003ch3 id=\"naming-image-tagging-and-alt-tags-in-dynamics-for-marketing\"\u003eNaming, Image Tagging, and Alt Tags in Dynamics for Marketing\u003c/h3\u003e\n\u003cp\u003eWhen uploading images into Dynamics for Marketing (D4M), it is important to name your image correctly, tag it with keywords, and give it an alt tag.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eImage Naming Format\u003c/strong\u003e -\n[Country or Department 2-digit Code]-[Year]-[Image Name]\u003c/p\u003e\n\u003cp\u003eExample: MS-2021-ISFG-PreEvent-39836731\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eKeywords for Image Tagging\u003c/strong\u003e -\nTag your uploaded images with CRM keywords that are associated with the content found in the image.\u003c/p\u003e\n\u003cp\u003e\u003cstrong\u003eAdd Alt Tag to All Images\u003c/strong\u003e -\nAfter adding an image to your email, click on the image to add an alt tag. This allows users that have HTML turned off or screen readers to be able to identify the content.\u003c/p\u003e\n\u003ch3 id=\"list-of-keywords\"\u003eList of Keywords\u003c/h3\u003e\n\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cul\u003e\n\u003cli\u003e3D Cell Culture\u003c/li\u003e\n\u003cli\u003eApoptosis/Cell Death/Cell Viability\u003c/li\u003e\n\u003cli\u003eBioassays/ Biologics\u003c/li\u003e\n\u003cli\u003eBody\u003c/li\u003e\n\u003cli\u003eCellular Imaging/In Vivo Imaging\u003c/li\u003e\n\u003cli\u003eCloning\u003c/li\u003e\n\u003cli\u003eCosmetics\u003c/li\u003e\n\u003cli\u003eDiagnostic NA Purification\u003c/li\u003e\n\u003cli\u003eDrug Discovery/ HTS\u003c/li\u003e\n\u003cli\u003eEnergy Metabolism\u003c/li\u003e\n\u003cli\u003eEpigenetics\u003c/li\u003e\n\u003cli\u003eFood/Feed/Plant/GMO\u003c/li\u003e\n\u003cli\u003eFooter\u003c/li\u003e\n\u003cli\u003eGeneric\u003c/li\u003e\n\u003cli\u003eHeader\u003c/li\u003e\n\u003cli\u003eHigh Throughput Purification\u003c/li\u003e\n\u003cli\u003eImmunoassay/ELISA\u003c/li\u003e\n\u003cli\u003eMass Spectrometry\u003c/li\u003e\n\u003cli\u003eMSI\u003c/li\u003e\n\u003cli\u003eNext Gen Sequencing\u003c/li\u003e\n\u003cli\u003eNucleic Acid Purification\u003c/li\u003e\n\u003cli\u003ePCR/qPCR\u003c/li\u003e\n\u003cli\u003ePromega Logo\u003c/li\u003e\n\u003cli\u003eProtein Degradation\u003c/li\u003e\n\u003cli\u003eProtein Expression\u003c/li\u003e\n\u003cli\u003eProtein Interactions\u003c/li\u003e\n\u003cli\u003eProtein Purification\u003c/li\u003e\n\u003cli\u003eProtein Tagging/CRISPR\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\u003c/li\u003e\n\u003cli\u003eReporter Assays\u003c/li\u003e\n\u003cli\u003eSanger Sequencing\u003c/li\u003e\n\u003cli\u003eSBU\u003c/li\u003e\n\u003cli\u003eSignaling/Kinase Assays\u003c/li\u003e\n\u003cli\u003eSocial\u003c/li\u003e\n\u003cli\u003eSTR Forensic\u003c/li\u003e\n\u003cli\u003eSTR Research/ Clinical\u003c/li\u003e\n\u003cli\u003eTransfection\u003c/li\u003e\n\u003cli\u003eVirology\u003c/li\u003e\n\u003cli\u003eWater Testing\u003c/li\u003e\n\u003cli\u003e1010 Madison\u003c/li\u003e\n\u003cli\u003e5010 BNL\u003c/li\u003e\n\u003cli\u003e5020 UK\u003c/li\u003e\n\u003cli\u003e5030 Switzerland\u003c/li\u003e\n\u003cli\u003e5040 France\u003c/li\u003e\n\u003cli\u003e5050 Germany\u003c/li\u003e\n\u003cli\u003e5051 Austria\u003c/li\u003e\n\u003cli\u003e5052 Poland\u003c/li\u003e\n\u003cli\u003e5060 Italy\u003c/li\u003e\n\u003cli\u003e5070 Spain\u003c/li\u003e\n\u003cli\u003e5080 Sweden\u003c/li\u003e\n\u003cli\u003e7010 Australia\u003c/li\u003e\n\u003cli\u003e7030 Japan\u003c/li\u003e\n\u003cli\u003e7040 Beijing\u003c/li\u003e\n\u003cli\u003e7050 Singapore\u003c/li\u003e\n\u003cli\u003e7070 Korea\u003c/li\u003e\n\u003cli\u003e7080 India\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:13,href:"/docs/email-style-guide/email-examples/",title:"Email Examples",description:"",content:"\u003ch2 id=\"email-design-samples\"\u003eEmail Design Samples\u003c/h2\u003e\n\u003cp\u003eThe following email designs represent how to properly use the header, footer, and typographic styles.\u003c/p\u003e\n\u003cp\u003eEach sample has a clear sol call to action button and represents the tone and style of a Promega email.\u003c/p\u003e\n\u003ch3 id=\"example-email-1\"\u003eExample Email 1\u003c/h3\u003e\n\u003cimg src=\"/images/email-style-guide/example-emails/promega-brand-email-example-1.png\" class=\"guideline-img\" /\u003e\n\n\u003ch3 id=\"example-email-2\"\u003eExample Email 2\u003c/h3\u003e\n\u003cimg src=\"/images/email-style-guide/example-emails/promega-brand-email-example-2.png\" class=\"guideline-img\" /\u003e\n\n\u003ch3 id=\"example-email-3\"\u003eExample Email 3\u003c/h3\u003e\n\u003cimg src=\"/images/email-style-guide/example-emails/promega-brand-email-example-3.png\" class=\"guideline-img\" /\u003e\n\n"},{id:14,href:"/docs/prologue/",title:"About",description:"About Nebula",content:""},{id:15,href:"/docs/components/",title:"Components",description:"Components of the design system",content:""},{id:16,href:"/docs/",title:"Docs",description:"Docs Doks.",content:""},];index.add(docs);userinput.addEventListener('input',show_results,true);suggestions.addEventListener('click',accept_suggestion,true);function show_results(){var value=this.value;var results=index.search(value,5);var entry,childs=suggestions.childNodes;var i=0,len=results.length;suggestions.classList.remove('d-none');results.forEach(function(page){entry=document.createElement('div');entry.innerHTML='<a href><span></span><span></span></a>';a=entry.querySelector('a'),t=entry.querySelector('span:first-child'),d=entry.querySelector('span:nth-child(2)');a.href=page.href;t.textContent=page.title;d.textContent=page.description;suggestions.appendChild(entry);});while(childs.length>len){suggestions.removeChild(childs[i])}}
function accept_suggestion(){while(suggestions.lastChild){suggestions.removeChild(suggestions.lastChild);}
return false;}}());