var suggestions=document.getElementById('suggestions');var userinput=document.getElementById('userinput');document.addEventListener('keydown',inputFocus);function inputFocus(e){if(e.keyCode===191){e.preventDefault();userinput.focus();}
if(e.keyCode===27){userinput.blur();suggestions.classList.add('d-none');}}
document.addEventListener('click',function(event){var isClickInsideElement=suggestions.contains(event.target);if(!isClickInsideElement){suggestions.classList.add('d-none');}});document.addEventListener('keydown',suggestionFocus);function suggestionFocus(e){const focusableSuggestions=suggestions.querySelectorAll('a');const focusable=[...focusableSuggestions];const index=focusable.indexOf(document.activeElement);let nextIndex=0;if(e.keyCode===38){e.preventDefault();nextIndex=index>0?index-1:0;focusableSuggestions[nextIndex].focus();}
else if(e.keyCode===40){e.preventDefault();nextIndex=index+1<focusable.length?index+1:index;focusableSuggestions[nextIndex].focus();}}
(function(){var index=new FlexSearch({preset:'score',cache:true,doc:{id:'id',field:['title','description','content',],store:['href','title','description',],},});var docs=[{id:0,href:"/docs/components/buttons/",title:"Buttons",description:"",content:"\u003ch2 id=\"overview\"\u003eOverview\u003c/h2\u003e\n\u003cp\u003eWe use our primary brand color Sol sitewide to help users quickly identify available actions.\u003c/p\u003e\n\u003ch3 id=\"solid--primary\"\u003eSolid / Primary\u003c/h3\u003e\n\u003cp\u003eUsed for primary actions on the page. Uses solid sol background to draw user\u0026rsquo;s eye.\u003c/p\u003e\n\u003cp\u003eWe recommend only using one solid/primary action per major page section.\u003c/p\u003e\n\u003ch4 id=\"live-example\"\u003eLive Example\u003c/h4\u003e\n\u003cp\u003e\u003cem\u003eNote: This represents a future design that we are working to implement.\u003c/em\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"wvoXQEq\"\n    data-user=\"catertot\"\n    data-height=\"202\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\u003c/p\u003e\n\u003ch3 id=\"outline--secondary\"\u003eOutline / Secondary\u003c/h3\u003e\n\u003cp\u003eUsed for supporting or non-primary page actions (e.g., additional resources, related products)\u003c/p\u003e\n\u003ch4 id=\"live-example-1\"\u003eLive example\u003c/h4\u003e\n\u003cp\u003e\u003cem\u003eNote: This represents a future design that we are working to implement.\u003c/em\u003e\n\n\n\n\n\n\n\n\u003cscript\n    data-slug-hash=\"NWbmVPW\"\n    data-user=\"catertot\"\n    data-height=\"202\"\n    data-default-tab=\"result\"\n    data-theme-id=\"39481\"\n    class='codepen'\n    async\n    src=\"//codepen.io/assets/embed/ei.js\"\n\u003e\u003c/script\u003e\n\u003c/p\u003e\n\u003c!-- ### Link Style --\u003e\n\u003c!-- Do we want this? Idk --\u003e\n\u003c!-- ### Full Width\n\nDisplays at full width of its container.\n\n[example here] --\u003e\n\u003c!-- ### Accessibility\n[under construction] --\u003e\n\u003ch2 id=\"guidelines\"\u003eGuidelines\u003c/h2\u003e\n\u003ch3 id=\"use-buttons-when\"\u003eUse Buttons When\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eTriggering an action\u003c/li\u003e\n\u003cli\u003eEnabling a “final” action (e.g., Check Out, Add to Cart)\u003c/li\u003e\n\u003cli\u003eMoving forward or backward through a flow\u003c/li\u003e\n\u003cli\u003eSubmitting a form or requested information\u003c/li\u003e\n\u003cli\u003eConfirming completion of a flow or cancelling out of it\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id=\"dont-use-buttons-when\"\u003eDon\u0026rsquo;t Use Buttons When\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eTaking users to a different section of the same page. Instead, use Links\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch3 id=\"do--dont\"\u003eDo / Don\u0026rsquo;t\u003c/h3\u003e\n\u003cp\u003e\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/do-bg-gradient.png\" class=\"guideline-img\" /\u003e\n\nDo make sure buttons are visible against backgrounds.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n\u003cimg src=\"/images/components/buttons/dont-busy-bg.png\" class=\"guideline-img\" /\u003e\n\nDon\u0026rsquo;t use an outline button on a busy background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\u003c/p\u003e\n\u003cp\u003e\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n[Add Screenshot]\u003c/p\u003e\n\u003cp\u003eDo check contrast between button and background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n[Add Screenshot]\u003c/p\u003e\n\u003cp\u003eDon\u0026rsquo;t use a button against a sol background.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\u003c/p\u003e\n\u003cp\u003e\u003cdiv class=\"splitbox\"\u003e\u003cdiv class=\"left\"\u003e\n\n\u003cdiv class=\"guideline-container__correct\"\u003e\n  \u003ch3 class=\"guideline-heading__correct\"\u003eDo\u003c/h3\u003e\n\n\n\n  \n\n[Add Screenshot]\u003c/p\u003e\n\u003cp\u003eMatch button and text alignment.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv class=\"right\"\u003e\n\n\u003cdiv class=\"guideline-container__incorrect\"\u003e\n  \u003ch3 class=\"guideline-heading__incorrect\"\u003eDon't\u003c/h3\u003e\n\n\n  \n\n[Add Screenshot]\u003c/p\u003e\n\u003cp\u003eDon\u0026rsquo;t vary alignment of content within a container.\n\u003c/div\u003e\n\n\u003c/div\u003e\u003cdiv style=\"clear:both\"\u003e\u003c/div\u003e\u003c/div\u003e\n\u003c/p\u003e\n\u003ch2 id=\"modifiers\"\u003eModifiers\u003c/h2\u003e\n\u003cp\u003eHere\u0026rsquo;s info on the Sitecore modifiers used to create buttons.\u003c/p\u003e\n"},{id:1,href:"/docs/prologue/introduction/",title:"Introduction",description:"An introduction to the Nebula design system",content:"\u003cp\u003eNebula is the design system for Promega, a biotech company based in Madison, WI, USA. It is a central resource for component information, design rationale, and best practices.\u003c/p\u003e\n\u003cp\u003ePromega.com uses Sitecore as our content management system. Sitecore is managed by the WUX team in Marketing Services.\u003c/p\u003e\n\u003ch2 id=\"overview-of-site-sections\"\u003eOverview of Site Sections\u003c/h2\u003e\n\u003cul\u003e\n\u003cli\u003e\u003cstrong\u003eGetting Started\u003c/strong\u003e: This section includes information about Nebula itself, as well as basic information about our site. Check here for browser support, breakpoints, and additional resources.\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eFoundations\u003c/strong\u003e: Here\u0026rsquo;s where we share our design rationale for foundational elements. Some information can be found on our \u003ca href=\"https://promega.widencollective.com/portals/rrxinhb5/PromegaBrand\"\u003eBrand Portal\u003c/a\u003e, but we use Nebula to further define web-specific styles and patterns.\u003c/li\u003e\n\u003cli\u003e\u003cstrong\u003eComponents\u003c/strong\u003e: Detailed information on our Sitecore components. This includes styles and variants, modifier usage, and best practices.\u003c/li\u003e\n\u003c/ul\u003e\n"},{id:2,href:"/docs/getting-started/browser-info/",title:"Browser Info",description:"",content:"\u003ch2 id=\"supported-browsers\"\u003eSupported Browsers\u003c/h2\u003e\n\u003cp\u003eThe promega.com website supports the following browsers:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eChrome\u003c/li\u003e\n\u003cli\u003eFirefox\u003c/li\u003e\n\u003cli\u003eSafari\u003c/li\u003e\n\u003cli\u003eEdge\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"display-breakpoints\"\u003eDisplay Breakpoints\u003c/h2\u003e\n\u003cp\u003ePromega.com has six breakpoints, named for science stuff.\u003c/p\u003e\n\u003ctable\u003e\n\u003cthead\u003e\n\u003ctr\u003e\n\u003cth style=\"text-align:left\"\u003eBreakpoint Name\u003c/th\u003e\n\u003cth style=\"text-align:center\"\u003eRange\u003c/th\u003e\n\u003c/tr\u003e\n\u003c/thead\u003e\n\u003ctbody\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eGene\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e0-400px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eChromosome\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e401-480px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eNucleus\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e481-600px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eCell\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e601-768px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eColony\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e769-1024px\u003c/td\u003e\n\u003c/tr\u003e\n\u003ctr\u003e\n\u003ctd style=\"text-align:left\"\u003eOrganism\u003c/td\u003e\n\u003ctd style=\"text-align:center\"\u003e1025-1280px\u003c/td\u003e\n\u003c/tr\u003e\n\u003c/tbody\u003e\n\u003c/table\u003e\n"},{id:3,href:"/docs/foundations/color/",title:"Color",description:"",content:"\u003ch2 id=\"promega-brand\"\u003ePromega Brand\u003c/h2\u003e\n\u003cp\u003eInformation on Promega\u0026rsquo;s brand colors can be found on the \u003ca href=\"https://promega.widencollective.com/portals/ipuxspqm/CopyofColorPromegaBrand\"\u003eBrand Portal\u003c/a\u003e.\u003c/p\u003e\n"},{id:4,href:"/docs/foundations/typography/",title:"Typography",description:"",content:"\u003ch2 id=\"promega-brand\"\u003ePromega Brand\u003c/h2\u003e\n\u003cp\u003eInformation on Promega\u0026rsquo;s brand typography can be found on the \u003ca href=\"https://promega.widencollective.com/portals/op7opfpe/CopyofTypographyPromegaBrand\"\u003eBrand Portal\u003c/a\u003e.\u003c/p\u003e\n"},{id:5,href:"/docs/prologue/release-notes/",title:"Release Notes",description:"",content:"\u003cp\u003eThis page will list details about our updates and releases to the system.\u003c/p\u003e\n\u003ch2 id=\"latest-release\"\u003eLatest Release\u003c/h2\u003e\n\u003ch3 id=\"v10---april-2021\"\u003ev1.0 - April 2021\u003c/h3\u003e\n\u003cp\u003eOur initial release! After many months of work, we\u0026rsquo;re excited to unveil the Nebula design system.\u003c/p\u003e\n\u003cp\u003eOur first release consists of:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eSetting up the site (introductory content, contact forms, overall purpose)\u003c/li\u003e\n\u003cli\u003eDefining button components\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"future-releases\"\u003eFuture Releases\u003c/h2\u003e\n\u003cp\u003eFor our next release, we\u0026rsquo;re working on:\u003c/p\u003e\n\u003cul\u003e\n\u003cli\u003eAccessibility guidelines and best practices\u003c/li\u003e\n\u003cli\u003eText link styles\u003c/li\u003e\n\u003cli\u003eTile components\u003c/li\u003e\n\u003c/ul\u003e\n\u003cp\u003eWant to request a specific component or feature? Use our \u003ca href=\"https://promega.formstack.com/forms/nebula_request_feature\"\u003eRequest a Feature\u003c/a\u003e form.\u003c/p\u003e\n"},{id:6,href:"/docs/prologue/contact-us/",title:"Contact Us",description:"",content:"\u003ch2 id=\"design-advisory-guild\"\u003eDesign Advisory Guild\u003c/h2\u003e\n\u003cp\u003eThe Design Advisory Guild (DAG) is a cross-functional team in Marketing Services. Blending our expertise in design, front-end development, and UX, our goal is to create a living resource for designing for Promega on the web.\u003c/p\u003e\n\u003cp\u003eIf you have questions or feedback, please fill out our \u003ca href=\"https://promega.formstack.com/forms/nebula_contact\"\u003eContact Form\u003c/a\u003e.\u003c/p\u003e\n\u003ch3 id=\"current-guild-members\"\u003eCurrent Guild Members\u003c/h3\u003e\n\u003cul\u003e\n\u003cli\u003eCate Amery\u003c/li\u003e\n\u003cli\u003eSean Rudi\u003c/li\u003e\n\u003cli\u003eJamie Xiong\u003c/li\u003e\n\u003cli\u003eJenna Stormberg\u003c/li\u003e\n\u003cli\u003eJason Ober\u003c/li\u003e\n\u003c/ul\u003e\n\u003ch2 id=\"report-an-issue\"\u003eReport an Issue\u003c/h2\u003e\n\u003cp\u003eFind something wrong? Please report it to us using this form: \u003ca href=\"https://promega.formstack.com/forms/nebula_report_issue\"\u003eReport an Issue\u003c/a\u003e\u003c/p\u003e\n\u003ch2 id=\"request-a-feature\"\u003eRequest a Feature\u003c/h2\u003e\n\u003cp\u003eAre we missing something? Let us know what you\u0026rsquo;d like us to add: \u003ca href=\"https://promega.formstack.com/forms/nebula_request_feature\"\u003eRequest a Feature\u003c/a\u003e\u003c/p\u003e\n"},{id:7,href:"/docs/getting-started/resources/",title:"Resources",description:"",content:"\u003ch2 id=\"promega-brand-portal\"\u003ePromega Brand Portal\u003c/h2\u003e\n\u003cp\u003eCheck out Promega\u0026rsquo;s \u003ca href=\"https://promega.widencollective.com/portals/rrxinhb5/PromegaBrand\" target=\"_blank\"\u003eBrand Portal\u003c/a\u003e\u003c/p\u003e\n"},{id:8,href:"/docs/prologue/",title:"About",description:"About Nebula",content:""},{id:9,href:"/docs/components/",title:"Components",description:"Components of the design system",content:""},{id:10,href:"/docs/",title:"Docs",description:"Docs Doks.",content:""},];index.add(docs);userinput.addEventListener('input',show_results,true);suggestions.addEventListener('click',accept_suggestion,true);function show_results(){var value=this.value;var results=index.search(value,5);var entry,childs=suggestions.childNodes;var i=0,len=results.length;suggestions.classList.remove('d-none');results.forEach(function(page){entry=document.createElement('div');entry.innerHTML='<a href><span></span><span></span></a>';a=entry.querySelector('a'),t=entry.querySelector('span:first-child'),d=entry.querySelector('span:nth-child(2)');a.href=page.href;t.textContent=page.title;d.textContent=page.description;suggestions.appendChild(entry);});while(childs.length>len){suggestions.removeChild(childs[i])}}
function accept_suggestion(){while(suggestions.lastChild){suggestions.removeChild(suggestions.lastChild);}
return false;}}());